#!/bin/bash

# Update my mendeley references.
# The references are automatically generated by mendeley in:
# /home/ray/mendeley_sorted/library.bib
# I did not use a symbolic link so we have a static copy
# for git.
if [ -f /home/ray/mendeley_sorted/library.bib ]
  then
#    cp --update /home/ray/mendeley_sorted/library.bib ./../Bibliographies/library.bib
cp --update /home/ray/mendeley_sorted/library.bib ./Bibliographies/library.bib
fi



